select count(*) from badges as b, comments as c, postlinks as pl, posts as p, users as u, votes as v where b.userid = p.owneruserid and c.postid = p.id and p.id = pl.relatedpostid and p.owneruserid = u.id and pl.relatedpostid = v.postid and c.score = 2 and p.commentcount <= 12 and p.creationdate >= 1280174302 and p.favoritecount <= 4 and p.favoritecount >= 0 and p.viewcount <= 7710 and u.upvotes >= 0;