select count(*) from badges as b, comments as c, posts as p, users as u, votes as v where b.userid = c.userid and c.userid = p.owneruserid and p.owneruserid = u.id and u.id = v.userid and c.score = 1 and p.commentcount = 0 and p.favoritecount >= 0 and p.score <= 23 and p.score >= -2 and p.viewcount <= 2432 and u.reputation <= 113 and u.reputation >= 1 and u.views <= 51 and u.views >= 0;