select count(*) from badges as b, comments as c, posthistory as ph, postlinks as pl, posts as p, votes as v where b.userid = c.userid and c.postid = p.id and p.id = ph.postid and ph.postid = pl.relatedpostid and pl.relatedpostid = v.postid and b.date <= 1410356990 and c.score = 0 and p.score <= 26 and v.bountyamount <= 500 and v.votetypeid = 2;