select count(*) from badges as b, comments as c, postlinks as pl, posts as p, users as u where b.userid = p.owneruserid and c.postid = p.id and p.id = pl.relatedpostid and p.owneruserid = u.id and b.date <= 1408896122 and c.score = 0 and p.posttypeid = 1 and p.viewcount >= 0 and pl.linktypeid = 3 and u.downvotes <= 0 and u.downvotes >= 0 and u.reputation <= 148 and u.upvotes <= 39 and u.upvotes >= 0;