select count(*) from comments as c, posthistory as ph, postlinks as pl, posts as p, votes as v where c.postid = p.id and p.id = ph.postid and ph.postid = pl.postid and pl.postid = v.postid and c.creationdate >= 1239495185 and c.score = 0 and p.commentcount >= 0 and p.favoritecount >= 0 and pl.linktypeid = 1 and v.votetypeid = 2;