select count(*) from badges as b, comments as c, posthistory as ph, posts as p, users as u where b.userid = c.userid and c.userid = p.lasteditoruserid and p.lasteditoruserid = ph.userid and ph.userid = u.id and b.date <= 1410564723 and p.answercount <= 5 and p.favoritecount <= 8 and p.score >= -2 and ph.posthistorytypeid = 5 and u.creationdate <= 1409058067 and u.downvotes <= 0 and u.upvotes >= 0 and u.views = 0;