select count(*) from comments as c, posthistory as ph, postlinks as pl, posts as p, users as u, votes as v where c.postid = p.id and p.id = ph.postid and p.lasteditoruserid = u.id and ph.postid = pl.postid and pl.postid = v.postid and p.creationdate <= 1410204003 and p.posttypeid = 1 and p.score <= 27 and p.score >= -1 and p.viewcount <= 5973 and p.viewcount >= 0 and pl.linktypeid = 1 and u.downvotes >= 0 and u.views = 0;