select count(*) from comments as c, posthistory as ph, postlinks as pl, posts as p, users as u, votes as v where c.postid = p.id and p.id = ph.postid and p.lasteditoruserid = u.id and ph.postid = pl.postid and pl.postid = v.postid and c.score = 1 and p.answercount >= 0 and p.creationdate <= 1410324041 and p.viewcount >= 0 and pl.creationdate >= 1304561000 and pl.linktypeid = 1 and u.downvotes <= 54 and u.downvotes >= 0;