select count(*) from comments as c, posthistory as ph, postlinks as pl, posts as p, users as u, votes as v where c.postid = p.id and p.id = ph.postid and p.lasteditoruserid = u.id and ph.postid = pl.postid and pl.postid = v.postid and c.creationdate <= 1410628049 and c.score = 0 and p.answercount >= 0 and p.commentcount >= 0 and ph.creationdate >= 1287681417 and ph.posthistorytypeid = 3 and u.downvotes <= 0 and u.upvotes <= 3 and u.upvotes >= 0 and v.votetypeid = 2;