select count(*) from comments as c, posthistory as ph, postlinks as pl, posts as p, users as u, votes as v where c.postid = p.id and c.userid = u.id and p.id = ph.postid and ph.postid = pl.postid and u.id = v.userid and c.creationdate <= 1410542776 and c.creationdate >= 1280146875 and p.commentcount <= 13 and p.score <= 19 and p.score >= -1 and ph.creationdate <= 1407384360 and ph.posthistorytypeid = 2 and u.creationdate <= 1408455194 and u.views >= 0 and v.bountyamount <= 50 and v.creationdate <= 1410624000 and v.creationdate >= 1279641600;