select count(*) from badges as b, comments as c, posthistory as ph, posts as p, users as u, votes as v where b.userid = p.owneruserid and c.postid = p.id and p.id = ph.postid and p.owneruserid = u.id and ph.postid = v.postid and p.commentcount <= 12 and p.commentcount >= 0 and p.posttypeid = 1 and p.viewcount <= 4159 and ph.posthistorytypeid = 2 and u.downvotes <= 1 and u.downvotes >= 0 and u.upvotes <= 6 and u.views <= 86 and v.votetypeid = 2;