select count(*) from badges as b, comments as c, posthistory as ph, postlinks as pl, posts as p, votes as v where b.userid = c.userid and c.postid = p.id and p.id = ph.postid and ph.postid = pl.relatedpostid and pl.relatedpostid = v.postid and c.creationdate >= 1279732783 and p.commentcount <= 9 and p.score >= -1 and p.viewcount >= 0 and ph.creationdate <= 1407344445 and ph.creationdate >= 1284975915 and v.votetypeid = 15;