select count(*) from badges as b, comments as c, posthistory as ph, posts as p, users as u, votes as v where b.userid = p.owneruserid and c.postid = p.id and p.id = ph.postid and p.owneruserid = u.id and ph.postid = v.postid and p.answercount <= 3 and p.answercount >= 0 and p.commentcount <= 22 and p.commentcount >= 0 and p.score <= 16 and u.upvotes <= 38;