select count(*) from badges as b, comments as c, posthistory as ph, posts as p, users as u, votes as v where b.userid = p.owneruserid and c.postid = p.id and p.id = ph.postid and p.owneruserid = u.id and ph.postid = v.postid and c.creationdate <= 1410263909 and c.score = 0 and p.answercount <= 4 and p.commentcount <= 11 and p.commentcount >= 0 and p.creationdate >= 1295915501 and p.score >= -4 and p.viewcount <= 5977 and p.viewcount >= 0 and u.downvotes <= 0 and u.reputation <= 312;