select count(*) from badges as b, comments as c, posthistory as ph, postlinks as pl, posts as p, users as u, votes as v where b.userid = c.userid and c.postid = p.id and c.userid = u.id and p.id = ph.postid and ph.postid = pl.relatedpostid and pl.relatedpostid = v.postid and c.creationdate >= 1280135388 and c.score = 0 and p.answercount >= 0 and p.commentcount <= 14 and p.commentcount >= 0 and p.posttypeid = 1 and ph.creationdate <= 1410437381 and pl.creationdate <= 1409822630 and pl.creationdate >= 1288144977 and v.creationdate <= 1410624000 and v.creationdate >= 1279641600;