select count(*) from comments as c, posthistory as ph, postlinks as pl, posts as p, users as u, votes as v where c.postid = p.id and p.id = ph.postid and p.owneruserid = u.id and ph.postid = pl.postid and pl.postid = v.postid and c.creationdate >= 1280144223 and p.commentcount <= 18 and p.creationdate <= 1410367990 and p.creationdate >= 1279691408 and p.score >= -2 and ph.creationdate >= 1290800325 and pl.creationdate <= 1407234471 and u.downvotes >= 0 and u.upvotes >= 0;