select count(*) from badges as b, comments as c, posthistory as ph, postlinks as pl, posts as p, users as u, votes as v where b.userid = c.userid and c.postid = p.id and c.userid = u.id and p.id = ph.postid and ph.postid = pl.relatedpostid and pl.relatedpostid = v.postid and b.date <= 1410477575 and b.date >= 1281062205 and c.score = 0 and p.answercount <= 5 and p.commentcount <= 12 and p.favoritecount >= 0 and p.viewcount >= 0 and pl.creationdate <= 1409561284 and pl.creationdate >= 1297857890 and pl.linktypeid = 1 and v.creationdate <= 1409414400 and v.creationdate >= 1279468800;