select count(*) from badges as b, comments as c, posthistory as ph, posts as p, users as u where b.userid = c.userid and c.userid = p.owneruserid and p.owneruserid = ph.userid and ph.userid = u.id and c.score = 0 and p.commentcount <= 12 and p.commentcount >= 0 and p.favoritecount <= 6 and p.favoritecount >= 0 and p.score >= -2 and ph.creationdate <= 1408323252 and u.downvotes <= 60 and u.downvotes >= 0 and u.views = 0;