select count(*) from badges as b, comments as c, posthistory as ph, posts as p, users as u, votes as v where b.userid = p.owneruserid and c.postid = p.id and p.id = ph.postid and p.owneruserid = u.id and ph.postid = v.postid and b.date <= 1410431708 and c.score = 0 and p.answercount <= 3 and p.favoritecount >= 0 and p.score <= 21 and u.reputation <= 240 and v.creationdate >= 1279468800;