select count(*) from comments as c, posthistory as ph, postlinks as pl, posts as p, users as u, votes as v where c.postid = p.id and c.userid = u.id and p.id = ph.postid and ph.postid = pl.postid and u.id = v.userid and c.creationdate <= 1410432309 and c.creationdate >= 1279578388 and c.score = 0 and p.favoritecount <= 2 and p.favoritecount >= 0 and p.posttypeid = 1 and ph.posthistorytypeid = 5 and u.creationdate >= 1284746321 and u.downvotes <= 0 and u.upvotes >= 0;