select count(*) from comments as c, posthistory as ph, postlinks as pl, posts as p, users as u, votes as v where c.postid = p.id and c.userid = u.id and p.id = ph.postid and ph.postid = pl.postid and u.id = v.userid and c.creationdate <= 1410336563 and c.creationdate >= 1280752068 and c.score = 0 and p.posttypeid = 1 and p.score = 4 and p.viewcount <= 4937 and ph.posthistorytypeid = 1 and pl.creationdate >= 1320268175 and u.downvotes >= 0 and u.reputation <= 270 and u.views <= 51 and u.views >= 0;