select count(*) from comments as c, posthistory as ph, postlinks as pl, posts as p, users as u, votes as v where c.postid = p.id and p.id = ph.postid and p.lasteditoruserid = u.id and ph.postid = pl.postid and pl.postid = v.postid and c.creationdate >= 1279574964 and c.score = 0 and p.viewcount >= 0 and u.creationdate <= 1409750325 and u.downvotes >= 0 and u.reputation >= 1 and u.upvotes >= 0;