select count(*) from comments as c, posthistory as ph, postlinks as pl, posts as p, users as u where c.postid = p.id and c.userid = u.id and p.id = ph.postid and ph.postid = pl.relatedpostid and c.creationdate <= 1410539652 and c.creationdate >= 1279690869 and p.creationdate <= 1408451742 and p.creationdate >= 1280583239 and ph.creationdate <= 1407500755 and ph.creationdate >= 1285764661 and u.downvotes = 0 and u.upvotes >= 0;