select count(*) from comments as c, posthistory as ph, postlinks as pl, posts as p, users as u, votes as v where c.postid = p.id and p.id = ph.postid and p.lasteditoruserid = u.id and ph.postid = pl.postid and pl.postid = v.postid and c.creationdate <= 1410435969 and c.score = 0 and p.commentcount >= 0 and p.favoritecount >= 0 and ph.posthistorytypeid = 5 and u.reputation <= 230 and u.views >= 0 and v.creationdate <= 1408723200;