select count(*) from badges as b, comments as c, posthistory as ph, postlinks as pl, posts as p, votes as v where b.userid = c.userid and c.postid = p.id and p.id = ph.postid and ph.postid = pl.relatedpostid and pl.relatedpostid = v.postid and c.score = 1 and p.answercount <= 6 and p.answercount >= 0 and p.commentcount >= 0 and p.posttypeid = 1 and p.viewcount >= 0 and ph.posthistorytypeid = 5 and v.votetypeid = 2;