select count(*) from comments as c, posthistory as ph, postlinks as pl, posts as p, users as u where c.postid = p.id and c.userid = u.id and p.id = ph.postid and ph.postid = pl.relatedpostid and c.creationdate <= 1410414189 and c.creationdate >= 1278822305 and p.commentcount <= 14 and p.commentcount >= 0 and ph.creationdate >= 1281035693 and pl.linktypeid = 1 and u.downvotes <= 0 and u.downvotes >= 0 and u.reputation <= 491 and u.reputation >= 1;