select count(*) from badges as b, comments as c, posthistory as ph, postlinks as pl, posts as p, votes as v where b.userid = c.userid and c.postid = p.id and p.id = ph.postid and ph.postid = pl.relatedpostid and pl.relatedpostid = v.postid and c.creationdate <= 1410418423 and c.creationdate >= 1281312301 and p.answercount = 0 and p.commentcount >= 0 and p.favoritecount <= 8 and p.favoritecount >= 0 and p.posttypeid = 1 and p.score >= -2 and ph.posthistorytypeid = 2 and pl.creationdate <= 1409755238 and pl.creationdate >= 1303834590 and v.votetypeid = 2;