select count(*) from badges as b, comments as c, posthistory as ph, posts as p, users as u, votes as v where b.userid = p.owneruserid and c.postid = p.id and p.id = ph.postid and p.owneruserid = u.id and ph.postid = v.postid and c.creationdate >= 1280140621 and p.creationdate <= 1408288314 and p.favoritecount <= 4 and p.posttypeid = 2 and p.score >= -2 and u.downvotes <= 5 and u.downvotes >= 0 and u.reputation <= 493 and u.reputation >= 1 and v.bountyamount >= 0;